const base__url = "/dashboard";
export default {
  data: () => ({
    menu: false,
    drawer: null,
    menu__links: [],
    currentUser: ""
  }),
  created() {
    this.checkCurrentLogin();
    this.getDataUser();
    this.checkKategori();
  },
  methods: {
    checkKategori() {
      let data = JSON.parse(localStorage.getItem("user"));
      let data__kategori = data[0].kategori;
      let hak_akses = data[0].hak_akses;

      if (hak_akses == "aktif") {
        if (data__kategori === "Prodi") {
          this.menu__links = [
            { to: base__url + "/home", title: "Home", icon: "dashboard" },
            {
              to: base__url + "/new_posts",
              title: "Buat artikel",
              icon: "add"
            },
            {
              to: base__url + "/list_posts",
              title: "Daftar artikel",
              icon: "insert_drive_file"
            },
            {
              to: base__url + "/list_users",
              title: "Daftar dosen",
              icon: "people"
            },
            {
              to: base__url + "/History",
              title: "History Surat",
              icon: "people"
            },
            {
              to: base__url + "/pengaturan",
              title: "Pengaturan hak akses",
              icon: "settings"
            },

            {
              to: "/",
              title: "Ke halaman utama",
              icon: "arrow_back"
            }
          ];
        } else if (data__kategori === "Dosen") {
          this.menu__links = [
            // {
            //   to: base__url + "/list_surat",
            //   title: "Daftar surat",
            //   icon: "dashboard"
            // },
            // {
            //   to: base__url + "/account",
            //   title: "Pengaturan akun",
            //   icon: "settings"
            // },
            {
              to: "/",
              title: "Ke halaman utama",
              icon: "arrow_back"
            },
            {
              to: base__url + "/History-dosen",
              title: "History Surat",
              icon: "people"
            }
          ];
        }
      } else {
        this.menu__links = [
          {
            to: "/",
            title: "Ke halaman utama",
            icon: "arrow_back"
          }
        ];
      }
    },
    checkCurrentLogin: function() {
      if (!localStorage.getItem("isLogin")) {
        this.$router.replace(this.$route.query.redirect || "/login");
      }
    },
    getDataUser: function() {
      let data = JSON.parse(localStorage.getItem("user"));

      this.axios
        .get("/user?id=" + data[0].id)
        .then(result => {
          this.currentUser = result.data.data[0];
        })
        .catch(err => {
          console.log(err);
        });

      this.checkKategori();
    },
    logout: function(event) {
      event.preventDefault();
      localStorage.token = false;
      this.checkCurrentLogin();
      localStorage.removeItem("user");
      localStorage.setItem("isLogin", false);
      this.$router.replace(this.$route.query.redirect || "/login");
    }
  },
  metaInfo: {
    title:
      "Dashboard | Prodi Teknik Informatika - Universitas Amikom Purwokerto",
    // override the parent template and just use the above title only
    titleTemplate: null
  }
};
